export class UserComponent {
  constructor(router, authenticationService, userService, notificationService) {
    this.router = router;
    this.authenticationService = authenticationService;
    this.userService = userService;
    this.notificationService = notificationService;
    this.titleSubject = new BehaviorSubject('Users');
    this.titleAction$ = this.titleSubject.asObservable();
    this.subscriptions = [];
    this.editUser = new User();
    this.fileStatus = new FileUploadStatus();
  }

  ngOnInit() {
    this.user = this.authenticationService.getUserFromLocalCache();
    this.getUsers(true);
  }

  changeTitle(title) {
    this.titleSubject.next(title);
  }

  getUsers(showNotification) {
    this.refreshing = true;
    this.subscriptions.push(this.userService.getUsers().subscribe(response => {
      this.userService.addUsersToLocalCache(response);
      this.users = response;
      this.refreshing = false;

      if (showNotification) {
        this.sendNotification(NotificationType.SUCCESS, `${response.length} user(s) loaded successfully.`);
      }
    }, errorResponse => {
      this.sendNotification(NotificationType.ERROR, errorResponse.error.message);
      this.refreshing = false;
    }));
  }

  onSelectUser(selectedUser) {
    this.selectedUser = selectedUser;
    this.clickButton('openUserInfo');
  }

  onProfileImageChange(fileName, profileImage) {
    this.fileName = fileName;
    this.profileImage = profileImage;
  }

  saveNewUser() {
    this.clickButton('new-user-save');
  }

  onAddNewUser(userForm) {
    const formData = this.userService.createUserFormData(null, userForm.value, this.profileImage);
    this.subscriptions.push(this.userService.addUser(formData).subscribe(response => {
      this.clickButton('new-user-close');
      this.getUsers(false);
      this.fileName = null;
      this.profileImage = null;
      userForm.reset();
      this.sendNotification(NotificationType.SUCCESS, `${response.firstName} ${response.lastName} added successfully`);
    }, errorResponse => {
      this.sendNotification(NotificationType.ERROR, errorResponse.error.message);
      this.profileImage = null;
    }));
  }

  onUpdateUser() {
    const formData = this.userService.createUserFormData(this.currentUsername, this.editUser, this.profileImage);
    this.subscriptions.push(this.userService.updateUser(formData).subscribe(response => {
      this.clickButton('closeEditUserModalButton');
      this.getUsers(false);
      this.fileName = null;
      this.profileImage = null;
      this.sendNotification(NotificationType.SUCCESS, `${response.firstName} ${response.lastName} updated successfully`);
    }, errorResponse => {
      this.sendNotification(NotificationType.ERROR, errorResponse.error.message);
      this.profileImage = null;
    }));
  }

  onUpdateCurrentUser(user) {
    this.refreshing = true;
    this.currentUsername = this.authenticationService.getUserFromLocalCache().username;
    const formData = this.userService.createUserFormData(this.currentUsername, user, this.profileImage);
    this.subscriptions.push(this.userService.updateUser(formData).subscribe(response => {
      this.authenticationService.addUserToLocalCache(response);
      this.getUsers(false);
      this.fileName = null;
      this.profileImage = null;
      this.sendNotification(NotificationType.SUCCESS, `${response.firstName} ${response.lastName} updated successfully`);
    }, errorResponse => {
      this.sendNotification(NotificationType.ERROR, errorResponse.error.message);
      this.refreshing = false;
      this.profileImage = null;
    }));
  }

  onUpdateProfileImage() {
    const formData = new FormData();
    formData.append('username', this.user.username);
    formData.append('profileImage', this.profileImage);
    this.subscriptions.push(this.userService.updateProfileImage(formData).subscribe(event => {
      this.reportUploadProgress(event);
    }, errorResponse => {
      this.sendNotification(NotificationType.ERROR, errorResponse.error.message);
      this.fileStatus.status = 'done';
    }));
  }

  reportUploadProgress(event) {
    switch (event.type) {
      case HttpEventType.UploadProgress:
        this.fileStatus.percentage = Math.round(100 * event.loaded / event.total);
        this.fileStatus.status = 'progress';
        break;

      case HttpEventType.Response:
        if (event.status === 200) {
          this.user.profileImageURL = `${event.body.profileImageUrl}?time=${new Date().getTime()}`;
          this.sendNotification(NotificationType.SUCCESS, `${event.body.firstName}\'s profile image updated successfully`);
          this.fileStatus.status = 'done';
          break;
        } else {
          this.sendNotification(NotificationType.ERROR, `Unable to upload image. Please try again`);
          break;
        }

      default:
        `Finished all processes`;
    }
  }

  updateProfileImage() {
    this.clickButton('profile-image-input');
  }

  onLogOut() {
    this.authenticationService.logout();
    this.router.navigate(['/login']);
    this.sendNotification(NotificationType.SUCCESS, `You've been successfully logged out`);
  }

  onResetPassword(emailForm) {
    this.refreshing = true;
    const emailAddress = emailForm.value['reset-password-email'];
    this.subscriptions.push(this.userService.resetPassword(emailAddress).subscribe(response => {
      this.sendNotification(NotificationType.SUCCESS, response.message);
      this.refreshing = false;
    }, error => {
      this.sendNotification(NotificationType.WARNING, error.error.message);
      this.refreshing = false;
    }, () => emailForm.reset()));
  }

  onDeleteUder(username) {
    this.subscriptions.push(this.userService.deleteUser(username).subscribe(response => {
      this.sendNotification(NotificationType.SUCCESS, response.message);
      this.getUsers(false);
    }, error => {
      this.sendNotification(NotificationType.ERROR, error.error.message);
    }));
  }

  onEditUser(editUser) {
    this.editUser = editUser;
    this.currentUsername = editUser.username;
    this.clickButton('openUserEdit');
  }

  searchUsers(searchTerm) {
    const results = [];

    for (const user of this.userService.getUsersFromLocalCache()) {
      if (user.firstName.toLowerCase().indexOf(searchTerm.toLowerCase()) !== -1 || user.lastName.toLowerCase().indexOf(searchTerm.toLowerCase()) !== -1 || user.username.toLowerCase().indexOf(searchTerm.toLowerCase()) !== -1 || user.userID.toLowerCase().indexOf(searchTerm.toLowerCase()) !== -1) {
        results.push(user);
      }
    }

    this.users = results;

    if (results.length === 0 || !searchTerm) {
      this.users = this.userService.getUsersFromLocalCache();
    }
  }

  get isAdmin() {
    return this.getUserRole() === Role.ADMIN;
  }

  getUserRole() {
    return this.authenticationService.getUserFromLocalCache().role;
  }

  sendNotification(notificationType, message) {
    if (message) {
      this.notificationService.notify(notificationType, message);
    } else {
      this.notificationService.notify(notificationType, 'An error occurred. Please try again.');
    }
  }

  clickButton(buttonId) {
    document.getElementById(buttonId).click();
  }

  ngOnDestroy() {
    this.subscriptions.forEach(sub => sub.unsubscribe());
  }

}

